parameters.inputSchema:
{
  "$schema": "http://json-schema.org/draft-07/schema#",
  "title": "Sora2VideoGenerationRequest",
  "description": "Simplified schema for Sora 2 video generation with enhanced prompts",
  "type": "object",
  "required": ["prompt", "aspect_ratio", "duration"],
  "properties": {
    "prompt": {
      "type": "string",
      "description": "The fully enhanced, professionally structured prompt optimized for Sora 2 video generation with cinematography details, specific actions, lighting, and visual specifics",
      "minLength": 50,
      "maxLength": 4000
    },
    "aspect_ratio": {
      "type": "string",
      "enum": ["16:9", "9:16"],
      "description": "Video aspect ratio. 16:9 for landscape/cinematic, 9:16 for portrait/social media"
    },
    "duration": {
      "type": "integer",
      "enum": [4, 8, 12],
      "description": "Video duration in seconds. 4s is most reliable, 8s and 12s may have reduced instruction-following accuracy"
    }
  }
}
